mapper.select:
select
`id`,`package_name`,`service_name`,`method_name`,`service_group`,`service_version`
,`proto_context`,`proto_req`,`proto_rep`,`gmt_create` ,`gmt_modified`
from gateway_grpc

	 and id = #{id} 
	 and package_name = #{packageName} 
	 and service_name = #{serviceName} 
	 and method_name = #{methodName} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from gateway_grpc

     and package_name = #{packageName} 
	 and service_name = #{serviceName} 
	 and method_name = #{methodName}

mapper.update:
update gateway_grpc
and package_name = #{packageName} `service_name`=#{serviceName}, `method_name`=#{methodName}, `service_group`=#{serviceGroup}, `service_version`=#{serviceVersion}, `proto_context` =#{protoContext}, `proto_req`=#{protoReq}, `proto_rep`=#{protoRep}, `gmt_modified`=sysdate()
where id = #{id}
