mapper.select:
SELECT invokeTime/1000 as invokeTime, SUM(concurrent) as sumconcurrent, SUM(elapsed) as sumelapsed, SUM(success) as sumsuccess, SUM(failure) as sumfailure, SUM(input) as suminput FROM saluki_invoke WHERE 1 = 1
AND service=#{service}
AND type=#{type}
GROUP BY invokeTime/1000

SELECT * FROM saluki_invoke WHERE 1 = 1
AND service=#{service}
AND type=#{type}
ORDER BY invokeDate DESC
