mapper.select:
SELECT
date_format(invokeDate,"%Y-%m-%d") as invokeDate,
date_format(invokeDate,"%Y-%m-%d %H") as invokeDate,
sum(concurrent) as sumConcurrent, sum(success) as sumSuccess, sum(failure) as sumFailure, sum(input) as sumInput, sum(output) as sumOutput, sum(elapsed) as sumElapsed, max(maxInput) as maxInput, max(maxOutput) as maxOutput, max(maxElapsed) as maxElapsed, max(maxConcurrent) as maxConcurrent FROM saluki_invoke WHERE 1 = 1
AND service=#{service}
AND type=#{type}
AND consumer=#{ip}
AND provider=#{ip}
AND invokeDate >= DATE_FORMAT(#{invokeDateFrom},'%Y-%m-%d')
AND invokeDate <= DATE_FORMAT(#{invokeDateTo},'%Y-%m-%d')
group by date_format(invokeDate,"%Y-%m-%d")
group by date_format(invokeDate,"%Y-%m-%d %H")
ORDER BY invokeDate DESC
